import{_ as E,e as B}from"./app.340c2b94.js";const A={},p=B('<h1 id="\u601D\u7EF4\u6CD5\u5219\u7814\u7A76-\u56E0\u679C\u5173\u7CFB\u4E0E\u903B\u8F91\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#\u601D\u7EF4\u6CD5\u5219\u7814\u7A76-\u56E0\u679C\u5173\u7CFB\u4E0E\u903B\u8F91\u5173\u7CFB" aria-hidden="true">#</a> \u601D\u7EF4\u6CD5\u5219\u7814\u7A76-\u56E0\u679C\u5173\u7CFB\u4E0E\u903B\u8F91\u5173\u7CFB</h1><h2 id="_1-\u4EBA\u5982\u4F55\u4E0B\u5224\u65AD-\u5173\u4E8E\u4EBA\u7684\u8BA4\u77E5\u4E0E\u5224\u65AD" tabindex="-1"><a class="header-anchor" href="#_1-\u4EBA\u5982\u4F55\u4E0B\u5224\u65AD-\u5173\u4E8E\u4EBA\u7684\u8BA4\u77E5\u4E0E\u5224\u65AD" aria-hidden="true">#</a> 1. \u4EBA\u5982\u4F55\u4E0B\u5224\u65AD\uFF1F\u5173\u4E8E\u4EBA\u7684\u8BA4\u77E5\u4E0E\u5224\u65AD</h2><p><strong>\u4EC0\u4E48\u662F\u601D\u7EF4\u6D3B\u52A8\uFF1F</strong></p><p>\u601D\u7EF4\u6D3B\u52A8\u5C31\u662F\u8BA4\u77E5\u3001\u5224\u65AD\u548C\u63A8\u7406\u3002\u601D\u7EF4\u6D3B\u52A8\u662F\u8BED\u8A00\u6D3B\u52A8\uFF0C\u6CA1\u6709\u79BB\u5F00\u8BED\u8A00\u7684\u601D\u7EF4\u6D3B\u52A8\u3002</p><p>\u6211\u4EEC\u7684\u601D\u7EF4\u6D3B\u52A8\u5C31\u662F\u6C49\u8BED\u8A00\u7684\u6D3B\u52A8\uFF0C\u6C49\u8BED\u81EA\u8EAB\u7684\u7279\u70B9\u5F88\u5927\u7A0B\u5EA6\u4E0A\u9650\u5236\u6216\u62D3\u5C55\u4E86\u6211\u4EEC\u7684\u601D\u7EF4\u7A7A\u95F4\u548C\u6240\u8C13\u6D3B\u52A8\u7684\u65B9\u5F0F\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u8BF4\u601D\u7EF4\u6D3B\u52A8\u662F\u8BED\u8A00\u6D3B\u52A8\uFF1F\u5982\u679C\u4E00\u4E2A\u5C0F\u5B69\u4E0D\u5B66\u8BED\u8A00\uFF0C\u5177\u6709\u601D\u7EF4\u5417\uFF1F</strong></p><p>\u4E00\u70B9\u8BED\u8A00\u90FD\u4E0D\u4F1A\u7684\uFF0C\u5C31\u4E0D\u4F1A\u5177\u6709\u4EBA\u7684\u601D\u7EF4\u3002\u4ED6\u7684\u601D\u7EF4\u6D3B\u52A8\u505C\u7559\u5728\u52A8\u7269\u5C42\u6B21\u3002\u52A8\u7269\u662F\u6709\u601D\u7EF4\u6D3B\u52A8\uFF0C\u4F46\u90A3\u53EA\u662F\u52A8\u7269\u7684\u601D\u7EF4\u3002\u8BED\u8A00\u662F\u62BD\u8C61\u7684\uFF0C\u201C\u98DF\u7269\u201D\u6307\u4EC0\u4E48\uFF0C\u6211\u4EEC\u90FD\u77E5\u9053\uFF0C\u4F46\u4E0D\u628A\u5177\u4F53\u7684\u98DF\u7269\u653E\u5728\u52A8\u7269\u9762\u524D\uFF0C\u4ED6\u5C31\u4E0D\u4F1A\u8BA4\u4E3A\u90A3\u662F\u98DF\u7269\u3002</p><p><strong>\u600E\u6837\u8FDB\u884C\u8BA4\u77E5\u3001\u5224\u65AD\u548C\u63A8\u7406\uFF1F</strong></p><p>\u601D\u7EF4\u6D3B\u52A8\u5177\u6709\u884C\u4E3A\u89C4\u5219\uFF0C\u4EFB\u4F55\u4F53\u80B2\u6BD4\u8D5B\u90FD\u6709\u5F62\u5F0F\u89C4\u5219\uFF0C\u601D\u7EF4\u7684\u5F62\u5F0F\u89C4\u5219\u53EB\u505A\u5F62\u5F0F\u903B\u8F91<a href="%E7%94%B1%E4%BA%9A%E9%87%8C%E5%A3%AB%E5%A4%9A%E5%BE%B7%E6%8F%90%E5%87%BA">^1</a>\u3002\u5BF9\u4E8B\u7269\u672C\u8EAB\u7684\u8BA4\u77E5\u4E5F\u6709\u4E00\u4E9B\u81EA\u8EAB\u7684\u903B\u8F91\u5173\u7CFB\uFF0C\u9ED1\u683C\u5C14\u628A\u5B83\u53EB\u505A\u8FA9\u8BC1\u903B\u8F91<a href="%E9%BB%91%E6%A0%BC%E5%B0%94%E7%9A%84%E8%BE%A9%E8%AF%81%E9%80%BB%E8%BE%91%E6%98%AF%E4%B8%80%E4%B8%AA%E8%BE%A9%E8%AF%81%E5%8F%91%E5%B1%95%E7%9A%84%E7%90%86%E8%AE%BA%E4%BD%93%E7%B3%BB%E3%80%82%E5%AE%83%E7%AC%AC%E4%B8%80%E6%AC%A1%E6%8A%8A%E6%95%B4%E4%B8%AA%E8%87%AA%E7%84%B6%E7%9A%84%E5%8E%86%E5%8F%B2%E7%9A%84%E5%92%8C%E7%B2%BE%E7%A5%9E%E7%9A%84%E4%B8%96%E7%95%8C%E6%8F%8F%E5%86%99%E4%B8%BA%E4%B8%80%E4%B8%AA%E8%BF%87%E7%A8%8B%EF%BC%8C%E4%BB%8E%E8%80%8C%E7%8C%9C%E6%B5%8B%E5%88%B0%E5%AE%A2%E8%A7%82%E4%BA%8B%E7%89%A9%E7%9A%84%E8%BE%A9%E8%AF%81%E6%B3%95%EF%BC%8C%E6%98%AF%E5%BB%BA%E7%AB%8B%E5%9C%A8%E5%94%AF%E5%BF%83%E4%B8%BB%E4%B9%89%E5%9F%BA%E7%A1%80%E4%B9%8B%E4%B8%8A%E7%9A%84%E3%80%82">^2</a>\u3002\u4EFB\u4F55\u4EBA\u8FDB\u884C\u8BA4\u77E5\u3001\u5224\u65AD\u548C\u63A8\u7406\u90FD\u9700\u8981\u524D\u63D0\uFF1A\u77E5\u8BC6\u50A8\u5907\u3001\u8BA4\u77E5\u80FD\u529B\u3001\u601D\u7EF4\u8BAD\u7EC3\u3002\u8FD9\u4E9B\u524D\u63D0\u51B3\u5B9A\u4E86\u601D\u7EF4\u5224\u65AD\u7684<strong>\u5C42\u6B21</strong>\u3002\u770B\u7684\u4E66\u8D8A\u591A\uFF0C\u601D\u8003\u7684\u4E8B\u8D8A\u591A\uFF0C\u505A\u51FA\u63A8\u7406\u5224\u65AD\u7684\u5C42\u6B21\u8D8A\u9AD8\u3002</p><p>\u4EBA\u7684\u8BA4\u77E5\u3001\u5224\u65AD\u3001\u63A8\u7406\u548C\u5BF9\u8C61\u6709\u5173\u3001\u548C\u4ED6\u4EBA\u6709\u5173\u548C\u81EA\u8EAB\u6709\u5173\u3002</p><p>\u4EBA\u4EEC\u5728\u8BA4\u8BC6\u4E8B\u7269\u505A\u51FA\u5224\u65AD\u7684\u65F6\u5019\u8981\u505A\u51FA\u771F\u5047\u5224\u65AD\uFF08\u4E8B\u5B9E\u5173\u7CFB\uFF09\uFF0C\u5584\u6076\u597D\u574F\u5224\u65AD\uFF08\u4EF7\u503C\u5173\u7CFB\uFF09\u3002</p><p>\u8BA8\u8BBA\u56E0\u679C\u5173\u7CFB\u4E0E\u903B\u8F91\u5173\u7CFB\u4E0D\u5B58\u5728\u773C\u89C1\u4E3A\u5B9E\u3002\u5C31\u662F\u601D\u7EF4\u81EA\u8EAB\u7684\u6D3B\u52A8\uFF0C\u9075\u5FAA\u601D\u7EF4\u6D3B\u52A8\u81EA\u8EAB\u7684\u5F62\u5F0F\u903B\u8F91\uFF0C\u5F62\u5F0F\u89C4\u5219\uFF0C\u548C\u5BF9\u4E8B\u7269\u7684\u8BA4\u8BC6\u3002\u6BD4\u5982\u56FD\u9645\u5F62\u52BF\u7684\u8BA4\u77E5\u6D3B\u52A8\uFF0C\u5FAE\u751F\u7269\u7684\u8BA4\u77E5\u3002\u4E0D\u53D6\u51B3\u4E8E\u6211\u4EEC\u662F\u5426\u770B\u89C1\u3002</p><p>\u4E8C\u6218\u540E\u5F62\u6210\u4E86\u4EE5\u8054\u5408\u56FD\u7684\u51FA\u73B0\u4E3A\u4EE3\u8868\u7684\u4E16\u754C\u79E9\u5E8F\uFF0C\u65E9\u671F\u7531\u4E24\u5927\u9635\u8425\u5212\u5206\uFF0C\u5171\u540C\u6784\u6210\u4E00\u79CD\u56FD\u9645\u79E9\u5E8F\u3002\u8FD9\u662F\u4E00\u79CD\u653F\u6CBB\u5173\u7CFB\uFF0C\u56E0\u4E3A\u5404\u81EA\u9884\u8BBE\u7684\u524D\u63D0\u4E0D\u4E00\u6837\uFF0C\u6240\u4EE5\u4EA7\u751F\u4E86\u5BF9\u7ACB\u5173\u7CFB\u3002\u653F\u6CBB\u4E00\u5B9A\u6709\u654C\u6211\u5173\u7CFB\u7684\uFF0C\u5982\u679C\u6CA1\u6709\u5206\u6B67\uFF0C\u4E0D\u6784\u6210\u653F\u6CBB\u3002\u8FD9\u79CD\u5BF9\u7ACB\u662F\u516C\u654C\u610F\u4E49\u4E0A\u7684\u5BF9\u7ACB\uFF0C\u516C\u654C\u610F\u4E49\u4E0A\u7684\u5BF9\u7ACB\u5C31\u662F\u56FD\u5BB6\u3001\u6C11\u65CF\u7684\u5BF9\u7ACB\u3002</p><p><strong>\u6709\u6CA1\u6709\u8D85\u8D8A\u56FD\u5BB6\u6C11\u65CF\u8FD9\u79CD\u516C\u654C\u610F\u4E49\u4E0A\u7684\u5BF9\u7ACB\u4E4B\u4E0A\u7684\u4E00\u79CD\u4EF7\u503C\u8FFD\u6C42\uFF1F</strong></p><p>\u8FD9\u79CD\u4EF7\u503C\u8FFD\u6C42\u6211\u4EEC\u53EF\u4EE5\u53EB\u505A\u4F26\u7406\u610F\u8BC6\uFF0C\u8FD9\u79CD\u4F26\u7406\u610F\u8BC6\u53EF\u4EE5\u533A\u5206\u4E3A\uFF1A</p><ul><li><p>\u8D23\u4EFB\u4F26\u7406\uFF1A\u8003\u8651\u7684\u662F\u5E94\u7528\u6027\u7684\u76EE\u7684\uFF0C\u5F97\u5931\u3001\u7ED3\u679C\uFF0C\u6BCF\u4E2A\u4EBA\u751F\u6D3B\u5728\u8FD9\u4E2A\u4E16\u754C\u4E0A\u5904\u7406\u4E8B\u60C5\u90FD\u4E0D\u80FD\u79BB\u5F00\u8D23\u4EFB\u4F26\u7406</p></li><li><p>\u4FE1\u5FF5\u4F26\u7406\uFF1A\u5BF9\u5E94\u7528\u6027\u76EE\u7684\u4E0D\u5927\u8003\u8651\u773C\u4E0B\u4E2A\u4EBA\u3001\u56FD\u5BB6\u6C11\u65CF\u7684\u5F97\u5931\uFF0C\u6709\u66F4\u9AD8\u7684\u4E00\u79CD\u8FFD\u6C42\uFF0C\u8D85\u8D8A\u4E86\u66F4\u52A0\u5F62\u5F0F\u5316\uFF0C\u66F4\u52A0\u666E\u904D\u6027\u7684\u8FFD\u6C42\u3002</p><p>\u4FE1\u5FF5\u4F26\u7406\u662F\u4ECE\u4EBA\u6027\u7684\u6CD5\u5219\u4E2D\u63A8\u7406\u51FA\u6765\u7684\u3002</p></li></ul><p><strong>\u603B\u7ED3</strong></p><p>\u56E0\u679C\u5173\u7CFB\u4E0E\u903B\u8F91\u5173\u7CFB\u662F\u4E00\u79CD\u601D\u7EF4\u65B9\u5F0F\uFF0C\u601D\u7EF4\u65B9\u5F0F\u662F\u601D\u7EF4\u6D3B\u52A8\u7684\u5F62\u5F0F\u8981\u6C42\uFF0C\u601D\u7EF4\u6D3B\u52A8\u5C31\u662F\u8FDB\u884C\u8BA4\u77E5\u3001\u5224\u65AD\u3001\u63A8\u7406\uFF08\u56E0\u679C\u5173\u7CFB\u4E0E\u903B\u8F91\u5173\u7CFB\u662F\u8BA4\u77E5\u3001\u5224\u65AD\u3001\u63A8\u7406\u7684\u4E00\u79CD\u89C4\u5219\uFF09\uFF0C\u6D89\u53CA\u5230\u4E00\u79CD\u601D\u7EF4\u7684\u5F62\u5F0F\u6D3B\u52A8\u548C\u4E00\u79CD\u5BF9\u4E8B\u7269\u7684\u8BA4\u77E5\u5224\u65AD\u63A8\u7406\uFF0C\u6709\u5F62\u5F0F\u7684\u8981\u6C42\u548C\u6709\u5185\u5BB9\u7684\u8981\u6C42\uFF0C\u5176\u4E2D\u6D89\u53CA\u5230\u5F62\u5F0F\u89C4\u5219\u548C\u5185\u5BB9\u89C4\u5219\uFF0C\u6709\u4EF7\u503C\u7684\uFF0C\u4E8B\u5B9E\u7684\uFF0C\u5BF9\u8C61\u7684\uFF0C\u81EA\u6211\u7684\uFF0C\u5F62\u5F0F\u7684\uFF0C\u5185\u5BB9\u7684\uFF0C\u8FD9\u4E9B\u90FD\u662F\u5728\u601D\u7EF4\u6D3B\u52A8\u4E2D\u8981\u63D0\u51FA\u7684\u65B9\u9762\uFF0C\u4E0D\u4EE5\u773C\u89C1\u4E3A\u5B9E\uFF0C\u8BA8\u8BBA\u7684\u5C31\u662F\u601D\u7EF4\u81EA\u8EAB\u7684\u8FD0\u884C\u89C4\u5219\u3002</p><p>\u4E8C\u6218\u540E\u7684\u6574\u4E2A\u79E9\u5E8F\u5EFA\u7ACB\u4E8E\u4E24\u5927\u96C6\u56E2\u5BF9\u7ACB\u7684\u57FA\u7840\uFF0C\u8FD9\u79CD\u79E9\u5E8F\u6B63\u5728\u5D29\u89E3\uFF0C\u91CD\u65B0\u7EC4\u5408\u3002\u56E0\u4E3A\u4E8C\u6218\u540E\u7684\u6574\u4E2A\u79E9\u5E8F\u662F\u4E00\u79CD\u516C\u654C\u610F\u4E49\u7684\u5BF9\u7ACB\uFF0C\u8FD9\u79CD\u5BF9\u7ACB\u6709\u4E00\u79CD\u4F26\u7406\u610F\u8BC6\u4E0D\u540C\u7684\u8FFD\u6C42\uFF0C\u9884\u8BBE\u7684\u76EE\u6807\u4E0D\u4E00\u6837</p>',19);function r(t,e){return p}var n=E(A,[["render",r],["__file","\u601D\u7EF4\u6CD5\u5219\u7814\u7A76.html.vue"]]);export{n as default};
